using System;using System.Web.Mvc;using Microsoft.ApplicationInsights;namespace Omnicx.WebStore.Core.ErrorHandler{    [AttributeUsage(AttributeTargets.Class | AttributeTargets.Method, Inherited = true, AllowMultiple = true)]     public class AiHandleErrorAttribute : HandleErrorAttribute    {        public override void OnException(ExceptionContext filterContext)        {            if (filterContext != null && filterContext.HttpContext != null && filterContext.Exception != null)            {                //If customError is Off, then AI HTTPModule will report the exception                if (filterContext.HttpContext.IsCustomErrorEnabled)                {                       var ai = new TelemetryClient();                    ai.TrackException(filterContext.Exception);                }             }            // https://stackoverflow.com/questions/10199345/the-view-error-or-its-master-was-not-found            //https://github.com/bugsnag/bugsnag-dotnet/issues/41            //Base on above Link commented this            //  base.OnException(filterContext);        }    }}